import API from "../utils/api";
import React, {useState,useEffect } from "react";
const HomePage= ()=>{
    const [myState,setState]=useState(null)
    useEffect(()=>{
        API.seed()
    .then(res=>{
        const user=res.data.results;  
        setState(user);
    })
    },[]);
    sortA=()=>{
        const {name} =this.state
        name.sort((a, b) => a - b)    
        this.setState({ name })
    }
    sortD=()=>{
        const {name} =this.state
        name.sort((a, b) => a - b).reverse()
        name.sort((a, b) => a - b)    
        this.setState({ name })
    }
return(
    <div>
        {myState&&myState.map((Employee) => (
          <li className="list-group-item">
            <img src={Employee.picture.thumbnail}></img>
            {Employee.name.first+ " "+
            Employee.name.last+" Gender: "+Employee.gender}
          </li>
          
        ))}
        <button onClick={this.sortA} type="button">Sort by name Ascending</button>
        <button onClick={this.SortD} type="button">Sort by name Descending</button>
    </div>
)
}
export default HomePage;
